mapper.select:
SELECT 
	*
FROM 
	employee 

	del_sign=#{DEL_NORMAL}
	
		AND job_no=#{jobNo}
	
	
		AND login_name=#{loginName}
	
	
		AND phone=#{phone}
	
	
		AND status=#{status}

SELECT 
	*
FROM 
	employee 

	
		del_sign=#{queryObj.DEL_NORMAL}
		
			AND job_no=#{queryObj.jobNo}
		
		
			AND login_name=#{queryObj.loginName}
		
		
			AND phone=#{queryObj.phone}
		
		
			AND status=#{queryObj.status}
