mapper.select:
select count(*) from goods_source 

	
		and sku=#{sku}
	
	
		and source=#{source}
	
	
		and category_id=#{categoryId}

select * from goods_source 

	
		and sku=#{sku}
	
	
		and source=#{source}

select 
	url 
from 
	goods_source 

	
		and source=#{source}
	
	
		and category_id=#{categoryId}

mapper.update:
update 
	goods_source 
set 
	brand_id=#{brandId},
	url=#{url},
	
	update_date_time=#{updateDateTime}

	
		and sku=#{sku}
	
	
		and source=#{source}
